INSERT INTO `p_article` VALUES ('2134', '14', '13', '7种抗衰老养颜食物 必看', 'http://www.993js.com//upimg/allimg/110625/0910423K9-2.jpg', '\r\n　　香蕉\r\n　　香蕉含有丰富的维生素A、维生素B、维生素C、维生素E和铁质，还含有协调身体酸碱度平衡的磷和矿物质，是提神醒脑的最佳保健食品之一。\r\n\r\n　　酸奶\r\n　　酸奶不仅有助于消化，还能有效', 'admin', '1', '0', '0', '1475919009', '1', '1', '0');
