INSERT INTO `p_article` VALUES ('2057', '15', '17', '李嘉欣身材不走样私房秘籍', 'http://7tsyl4.com1.z0.glb.clouddn.com/147462171053371.jpg', '\r\n鸡肉可以提供肉品中最优良的蛋白质来源，胆固醇含量也较低，而且鸡肉尝起来清淡，没有强烈的气味，几乎适合用来制作任何风味的菜肴，不但不会和调味料有不协调的问题，反而使料理更容易融合一体，因此在世界各地', 'admin', '1', '0', '0', '1474621707', '1', '1', '0');
